package co.mok.pro.vo;

public class ExpertVo {
private int expertCode;
private String userId;
private String exIntroduce; //전문가 짧은 자기소개

public ExpertVo() {
	// TODO Auto-generated constructor stub
}

public int getExpertCode() {
	return expertCode;
}

public void setExpertCode(int expertCode) {
	this.expertCode = expertCode;
}

public String getUserId() {
	return userId;
}

public void setUserId(String userId) {
	this.userId = userId;
}

public String getExIntroduce() {
	return exIntroduce;
}

public void setExIntroduce(String exIntroduce) {
	this.exIntroduce = exIntroduce;
}


	
	
}
